// Generate invoice data
const orderData = $input.first().json;

// Calculate totals
const items = orderData.products.map(product => {
  const subtotal = product.quantity * product.price;
  return {
    name: product.name,
    quantity: product.quantity,
    price: product.price,
    subtotal: subtotal
  };
});

const subtotalAmount = items.reduce((sum, item) => sum + item.subtotal, 0);
const taxRate = 0.10; // 10% tax
const taxAmount = subtotalAmount * taxRate;
const grandTotal = subtotalAmount + taxAmount;

// Generate invoice number
const invoiceNumber = `INV-${Date.now()}`;

return {
  invoice_number: invoiceNumber,
  customer_name: orderData.customer_name,
  customer_email: orderData.email,
  customer_phone: orderData.phone,
  date: orderData.date,
  items: items,
  subtotal: subtotalAmount,
  tax_rate: taxRate,
  tax_amount: taxAmount,
  grand_total: grandTotal,
  notes: orderData.notes || ''
};

// Professional invoice HTML template
const data = $input.first().json;
const currentDate = new Date().toLocaleDateString();

const html = `<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Invoice ${data.invoice_number}</title>
  <style>
    body {
      font-family: 'Arial', sans-serif;
      margin: 0;
      padding: 20px;
      color: #333;
      line-height: 1.6;
    }
    .invoice-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 30px;
      padding-bottom: 20px;
      border-bottom: 3px solid #2c5aa0;
    }
    .company-info {
      text-align: left;
    }
    .company-info h1 {
      color: #2c5aa0;
      margin: 0;
      font-size: 2.5em;
    }
    .invoice-info {
      text-align: right;
    }
    .invoice-info h2 {
      color: #2c5aa0;
      margin: 0;
      font-size: 1.8em;
    }
    .customer-section {
      background: #f8f9fa;
      padding: 20px;
      margin: 20px 0;
      border-radius: 8px;
    }
    .customer-section h3 {
      color: #2c5aa0;
      margin-top: 0;
    }
    .items-table {
      width: 100%;
      border-collapse: collapse;
      margin: 30px 0;
      box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    }
    .items-table th {
      background: #2c5aa0;
      color: white;
      padding: 15px;
      text-align: left;
      font-weight: bold;
    }
    .items-table td {
      padding: 12px 15px;
      border-bottom: 1px solid #ddd;
    }
    .items-table tr:nth-child(even) {
      background: #f8f9fa;
    }
    .items-table tr:hover {
      background: #e3f2fd;
    }
    .totals-section {
      float: right;
      width: 300px;
      margin-top: 20px;
    }
    .totals-table {
      width: 100%;
      border-collapse: collapse;
    }
    .totals-table td {
      padding: 8px 15px;
      border-bottom: 1px solid #ddd;
    }
    .totals-table .total-row {
      background: #2c5aa0;
      color: white;
      font-weight: bold;
      font-size: 1.1em;
    }
    .footer {
      clear: both;
      margin-top: 50px;
      padding-top: 20px;
      border-top: 1px solid #ddd;
      text-align: center;
      color: #666;
    }
    .notes {
      margin: 30px 0;
      padding: 15px;
      background: #fff3cd;
      border: 1px solid #ffeaa3;
      border-radius: 5px;
    }
  </style>
</head>
<body>
  <div class="invoice-header">
    <div class="company-info">
      <h1>Your Company</h1>
      <p>123 Business Street<br>
      City, State 12345<br>
      Phone: (555) 123-4567<br>
      Email: info@yourcompany.com</p>
    </div>
    <div class="invoice-info">
      <h2>INVOICE</h2>
      <p><strong>Invoice #:</strong> ${data.invoice_number}<br>
      <strong>Date:</strong> ${data.date}<br>
      <strong>Generated:</strong> ${currentDate}</p>
    </div>
  </div>

  <div class="customer-section">
    <h3>Bill To:</h3>
    <p><strong>${data.customer_name}</strong><br>
    Email: ${data.customer_email}<br>
    Phone: ${data.customer_phone}</p>
  </div>

  <table class="items-table">
    <thead>
      <tr>
        <th>Description</th>
        <th style="text-align: center;">Quantity</th>
        <th style="text-align: right;">Unit Price</th>
        <th style="text-align: right;">Total</th>
      </tr>
    </thead>
    <tbody>
      ${data.items.map(item => `
        <tr>
          <td>${item.name}</td>
          <td style="text-align: center;">${item.quantity}</td>
          <td style="text-align: right;">$${item.price.toFixed(2)}</td>
          <td style="text-align: right;">$${item.subtotal.toFixed(2)}</td>
        </tr>
      `).join('')}
    </tbody>
  </table>

  <div class="totals-section">
    <table class="totals-table">
      <tr>
        <td>Subtotal:</td>
        <td style="text-align: right;">$${data.subtotal.toFixed(2)}</td>
      </tr>
      <tr>
        <td>Tax (${(data.tax_rate * 100).toFixed(0)}%):</td>
        <td style="text-align: right;">$${data.tax_amount.toFixed(2)}</td>
      </tr>
      <tr class="total-row">
        <td>TOTAL:</td>
        <td style="text-align: right;">$${data.grand_total.toFixed(2)}</td>
      </tr>
    </table>
  </div>

  ${data.notes ? `
  <div class="notes">
    <h4>Notes:</h4>
    <p>${data.notes}</p>
  </div>
  ` : ''}

  <div class="footer">
    <p>Thank you for your business!<br>
    <small>This invoice was generated automatically on ${currentDate}</small></p>
  </div>
</body>
</html>`;

return {
  html: html,
  invoice_number: data.invoice_number,
  customer_name: data.customer_name
};

